import React from 'react';
import axios from 'axios';
import { Button, Form, FormGroup, Card, Container } from 'react-bootstrap';
import './Login.css'

class Login extends React.Component {

  constructor(props) {
    super(props);
    this.state = { email: '', password: '' };
  }

  handleEmailChange = event => {this.setState({ email: event.target.value })}
  handlePasswordChange = event => {this.setState({ password: event.target.value })}

  handleLogin = (event) => {
    event.preventDefault();
    axios.post('http://192.168.1.165:8000/users/rest-auth/login/', { email: this.state.email, password: this.state.password }).then(response => {
      axios.get('http://192.168.1.165:8000/users/rest-auth/user/', {headers: {"Authorization": 'Token ' + response.data.key}} ).then(data => {
        
        console.log(data.data)
        this.props.history.push('/home');
      })
    }).catch(error => {
      
    });
  }

  handleRegister = () => {
    this.props.history.push('/register');
  }
    
  render(){
    return (
      <div className="Login-App">
        <Card className="card-login z-depth-5">
          <Card.Body>
          <Form>
              <Form.Group controlId="formBasicEmail">
                <Form.Label>Email address</Form.Label>
                <Form.Control type="email" placeholder="Enter email" />
                <Form.Text className="text-muted">
                  We'll never share your email with anyone else.
                </Form.Text>
              </Form.Group>

              <Form.Group controlId="formBasicPassword">
                <Form.Label>Password</Form.Label>
                <Form.Control type="password" placeholder="Password" />
              </Form.Group>
              <Form.Group controlId="formBasicCheckbox">
                <Form.Check type="checkbox" label="Check me out" />
              </Form.Group>
              <Button variant="primary" type="submit">
                Submit
              </Button>
            </Form>
          </Card.Body>
        </Card>
      </div>
    );
  }
}
export default Login;
