import request from './Request'

const TOKEN_KEY = 'jwt';

const APIService = {

  getUserData() {
    // Return current user data
    const token = localStorage.getItem(TOKEN_KEY)
    return request({
      url: '/users/rest-auth/user/',
      method: 'GET',
      headers: {"Authorization": 'Token ' + token}
    });
  }, 

}

export default APIService;
