import React, { Fragment } from 'react';
import {Image, ListGroup, Container, Card, Row, Col }  from 'react-bootstrap'
import { BrowserRouter as Router, Route} from 'react-router-dom'
import axios from 'axios';
import API from "../../Utils/API";
import NavBar  from '../../Components/Navbar'
import './Home.css'
class Home extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
            isLoading: true,
            images: null
        };
    }

    async componentDidMount() {
        try {
            await API.getHighestScoreImages().then(response => {
                this.setState ({
                    images: response,
                    isLoading: false
                })    
            })
        } catch (e) {
            console.log(`Axios request failed: ${e}`);
        }
        
    }

    listComponent = (item, index) => {
        return (
            <Col>
                <Row style={{padding:10}}> 
                    <Image src={item.image} height="160" style={{width: '100%',
    height: undefined,
    aspectRatio: 1,}}/>
                </Row>
            </Col>
        )
    }

    render(){
        if(this.state.isLoading === false){
            return(
                <Fragment>
                    <NavBar/>
                    <div className="App-home-page ">
                    <Container className="App-home-container">
                        <ListGroup>{
                            this.state.images.map((item, index) => (
                                this.listComponent(item, index)
                            ))}
                        </ListGroup>
                    </Container>
                    </div>
                </Fragment>
            )
        }
        return (
            <div>
                loading
            </div>
        )
    }
}
export default Home
