import React, { Fragment } from 'react';
import NavBar  from '../../Components/Navbar'
import {Image, ListGroup, Container, Card, Row, Col }  from 'react-bootstrap'

class Post extends React.Component {
    
    url = "https://www.audi.com/content/dam/ci/Fundamentals/Basics/Colours/04_Grautoene_Elemente/Audi_Brandplattform_Colours_Element-15.png?downsize=1200px:*&imwidth=1200"

    constructor(props) {
        super(props);
        this.state = {
            image: null,
            isLoading: true,
        };
    }

    async componentDidMount() {

    }

    showImage(){
        if(this.state.image != null){
            return (
                <Image src={this.state.image.image}/>
            )
        }
        return <Image src={this.url} width="70%" aspectRatio="1/2"/>
    }

    render(){
        return(
            <Fragment>
                <NavBar/>
                <div className="App-home-page ">
                <Container className="App-home-container">
                    <div style={{display: 'flex', alignItems: 'center', justifyContent: 'center', flexDirection:'column'}}> 
                        {this.showImage()}
                    </div>
                </Container>
                </div>
            </Fragment>
        )
    }
}
export default Post;
