import React from 'react';
import axios from 'axios';
import { Button, Form, FormGroup, Card, Container } from 'react-bootstrap';
import {login} from '../../Utils/Authentication'
import './Login.css'

class Login extends React.Component {

  constructor(props) {
    super(props);
    this.state = { email: '', password: '' };
  }

  handleEmailChange = event => {this.setState({ email: event.target.value })}
  handlePasswordChange = event => {this.setState({ password: event.target.value })}

  handleLogin = (event) => {
    event.preventDefault();
    console.log(this.state.email)
    axios.post('http://192.168.1.165:8000/users/rest-auth/login/', { email: this.state.email, password: this.state.password }).then(response => {
      axios.get('http://192.168.1.165:8000/users/rest-auth/user/', {headers: {"Authorization": 'Token ' + response.data.key}} ).then(data => {
        login(response.data.key, data.data)
        console.log(data.data)
        this.props.history.push('/home');
      })
    }).catch(error => {
      
    });
  }

  handleRegister(){
    this.props.history.push('/register');
  }
    
  render(){
    return (
      <div className="Login-App">
        <Card className="card-login z-depth-5">
          <Card.Body>
          <Form onSubmit={this.handleLogin}>
              <Form.Group controlId="formBasicEmail" onChange={this.handleEmailChange}>
                <Form.Label>Email address</Form.Label>
                <Form.Control type="email" placeholder="Enter email" />
              </Form.Group>

              <Form.Group controlId="formBasicPassword" onChange={this.handlePasswordChange}>
                <Form.Label>Password</Form.Label>
                <Form.Control type="password" placeholder="Password" />
              </Form.Group>

              <Form.Group>
                <Button variant="primary" type="submit" style={{width:"100%"}}>
                  Login
                </Button>
              </Form.Group>

              <Form.Group>
                <Button variant="secondary" style={{width:"100%"}} onClick={() => this.handleRegister()}>
                  Register
                </Button>
              </Form.Group>
            </Form>
          </Card.Body>
        </Card>
      </div>
    );
  }
}
export default Login;
