import React, { Fragment } from 'react';
import NavBar  from '../../Components/Navbar'
import {Image, ListGroup, Container, Card, Row, Col }  from 'react-bootstrap'
import {currentUser} from '../../Utils/Authentication'
import axios from 'axios';
import API from "../../Utils/API";

class User extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
            isLoading: true,
            name:null
        };
    }

    async componentDidMount() {
        const { match: { params } } = this.props;
        if(params.user_id == currentUser().id) {
            this.setState({
                name: currentUser().first_name
            })
        } else {
            API.getImageUser(params.user_id).then((value) => {
                this.setState({
                    name: value.first_name
                })
            })
        }
    }

    render(){
        return(
            <Fragment>
                <NavBar/>
                <div className="App-home-page ">
                <Container className="App-home-container">
                    <div state={{flex:1, }}>

                    </div>
                </Container>
                </div>
            </Fragment>
        )
    }
}
export default User
