import React, { Fragment } from 'react';
import NavBar  from '../../Components/Navbar'
import {Image, ListGroup, Container, Card, Row, Col }  from 'react-bootstrap'
import {currentUser} from '../../Utils/Authentication'
import axios from 'axios';
import API from "../../Utils/API";

class User extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
            isLoading: true,
            user: null
        };
    }

    async componentDidMount() {
        const { match: { params } } = this.props;
        var currentuser = currentUser()
        if(params.user_id === currentuser.id) {
            this.setState({
                user: currentuser,
                isLoading: false
            })
        } else {
            API.getImageUser(params.user_id).then((value) => {
                console.log(value)
                this.setState({
                    user: value,
                    isLoading: false
                })
            })
        }
    }

    columnText = (value, title) => {
        return (
            <Col>
                <b>{value}</b>
                <div>{title}</div>
            </Col>
        )
    }

    render(){
        if(this.state.isLoading == false){
            return(
                <Fragment>
                    <NavBar/>
                    <div className="App-home-page ">
                    <Container className="App-home-container">
                        <div style={{display: 'flex', alignItems: 'center', justifyContent: 'center', flexDirection:'column'}}>
                                           
                                <Image 
                                    src={this.state.user.profilepicture}
                                    style={{width: 150, height: 150, borderRadius: 150/ 2,}} 
                                />
                                <b style={{fontSize: 24}}>{this.state.user.first_name} {this.state.user.last_name}</b>
                        </div>
                        <Row>
                            {this.columnText("13", "value")}
                        </Row>
                    </Container>
                    </div>
                </Fragment>
            )
        }
        return(
            <div>Loading</div>
        )
    }
}
export default User
