import React, { Fragment } from 'react';
import NavBar  from '../../Components/Navbar'
import {Image, ListGroup, Container, Card, Row, Col }  from 'react-bootstrap'
import {currentUser} from '../../Utils/Authentication'
import axios from 'axios';
import API from "../../Utils/API";

class User extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
            isLoading: true,
            user: null
        };
    }

    async componentDidMount() {
        const { match: { params } } = this.props;
        var currentuser = localStorage.getItem('current_user')
        console.log(currentUser.id)
        if(params.user_id === currentuser.id) {
            this.setState({
                user: currentuser,
                isLoading: false
            })
        } else {
            API.getImageUser(params.user_id).then((value) => {
                this.setState({
                    user: value.first_name,
                    isLoading: false
                })
            })
        }
    }

    render(){
        if(this.state.isLoading == false){
            return(
                <Fragment>
                    <NavBar/>
                    <div className="App-home-page ">
                    <Container className="App-home-container">
                        <div>{this.state.user.id}</div>
                    </Container>
                    </div>
                </Fragment>
            )
        }
        return(
            <div>Loading</div>
        )
    }
}
export default User
