import React, { Fragment } from 'react';
import NavBar  from '../../Components/Navbar'
import {Image, ListGroup, Container, Card, Row, Col }  from 'react-bootstrap'
import API from "../../Utils/API";

class ImageInfo extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
            isLoading: true,
            image: null
        };
    }

    async componentDidMount() {
        const { match: { params } } = this.props;
        var image_id = params.image_id
        API.getImageInfo(image_id).then(response => {
            this.setState({
                image: response,
                isLoading: false
            })
        })
    }

    winlost(element){
        var win = elements.wins;
        var lost = elements.lost;
        
        return win.toString() + "/" + lost.toString();
    }

    columnText = (value, title) => {
        return (
            <Col>
                <b style={{fontSize:20}}>{value}</b>
                <div>{title}</div>
            </Col>
        )
    }

    render(){
        if(this.state.isLoading == false){
            return(
                <Fragment>
                    <NavBar/>
                    <div className="App-home-page ">
                    <Container className="App-home-container">
                        <div style={{display: 'flex', alignItems: 'center', justifyContent: 'center', flexDirection:'column'}}>
                            <b style={{fontSize:24}}>{this.state.image.name}</b>
                            <Image src={this.state.image.image} width="100%"/>
                        </div>
                        <Row style={{alignItems: 'center', justifyContent: 'center', alignContent: 'space-between', textAlign:'center'}}>
                            {this.columnText(this.state.image.score, "score")}
                            {this.columnText(this.state.image.lost, "score average")}
                            {this.columnText(this.state.image.name, "wins/lost")}
                        </Row>
                    </Container>
                    </div>
                </Fragment>
            )
        }
        return(
            <showSpinner/>
        )
    }
}
export default ImageInfo
