import React, { Fragment } from 'react';
import NavBar  from '../../Components/Navbar'
import ImageUploading from "react-images-uploading";
import {Image, ListGroup, Container, Card, Row, Col }  from 'react-bootstrap'

class Post extends React.Component {
    
    url = "data:image/jpeg;base64,"

    constructor(props) {
        super(props);
        this.state = {
            image: null,
            isLoading: true,
        };
    }

    async componentDidMount() {

    }

    onChange = (image) => {
        // data for submit
        console.log(image);
        this.setState({
            image: image
        })
    };

    uploadImage(){
        return (
            <ImageUploading
                onChange={this.onChange}
                maxFileSize={5}
                acceptType={["jpg", "gif", "png"]}
            > 
            {({ image, onImageUpload, onImageRemoveAll }) => (
            // write your building UI
            <div>
                <button onClick={onImageUpload}>Upload images</button>
                <button onClick={onImageRemoveAll}>Remove all images</button>

                {imageList.map((image) => (
                <div key={image.key}>
                    <img src={image.dataURL} />
                    <button onClick={image.onUpdate}>Update</button>
                    <button onClick={image.onRemove}>Remove</button>
                </div>
                ))}
            </div>
            )}
            </ImageUploading>
        )
    }

    showImage(){
        if(this.state.image != null){
            return (
                <Image src={this.state.image.image}/>
            )
        }
        return <Image src={this.url} width="70%"/>
    }

    render(){
        return(
            <Fragment>
                <NavBar/>
                <div className="App-home-page ">
                <Container className="App-home-container">
                    <div style={{display: 'flex', alignItems: 'center', justifyContent: 'center', flexDirection:'column'}}> 
                        {this.showImage()}
                        {this.uploadImage()}
                    </div>
                </Container>
                </div>
            </Fragment>
        )
    }
}
export default Post;
